class PerformanceMonitor{constructor(){this.metrics={},this.init()}init(){this.observePerformance(),this.observeResources(),this.observeLayoutShifts()}observePerformance(){"performance"in window&&window.addEventListener("load",()=>{setTimeout(()=>this.captureMetrics(),0)})}captureMetrics(){const e=performance.getEntriesByType("navigation")[0];e&&(this.metrics={"DOM Content Loaded":e.domContentLoadedEventEnd-e.domContentLoadedEventStart,"Load Complete":e.loadEventEnd-e.loadEventStart,"Total Load Time":e.loadEventEnd-e.fetchStart,"First Paint":this.getFirstPaint(),"Largest Contentful Paint":this.getLCP()},console.log("Local Performance Metrics:",this.metrics))}getFirstPaint(){const e=performance.getEntriesByType("paint").find(e=>"first-paint"===e.name);return e?e.startTime:null}getLCP(){return"PerformanceObserver"in window?new Promise(e=>{new PerformanceObserver(t=>{const r=t.getEntries(),n=r[r.length-1];e(n.startTime)}).observe({entryTypes:["largest-contentful-paint"]})}):null}observeResources(){if("PerformanceObserver"in window){new PerformanceObserver(e=>{e.getEntries().forEach(e=>{e.duration>1e3&&console.warn("Slow resource:",e.name,e.duration+"ms")})}).observe({entryTypes:["resource"]})}}observeLayoutShifts(){if("PerformanceObserver"in window){let e=0;new PerformanceObserver(t=>{for(const r of t.getEntries())r.hadRecentInput||(e+=r.value);e>.1&&console.warn("High CLS detected:",e)}).observe({entryTypes:["layout-shift"]})}}}new PerformanceMonitor;